INSERT INTO rhmzrs.pages (id, title, slug, html_content, created_at, updated_at, deleted_at) VALUES (2, 'Оснивање', 'osnivanje', '<p><strong>Републички хидрометеоролошки завод</strong> (у даљем тексту Завод) је основан Законом о министарствима („Службени гласник Српског народа у Босни и Херцеговини“, број: 11/92). Овим Законом је дефинисано да Завод врши стручне и друге послове који се односе на: развој и функционисање хидролошке, метеоролошке и сеизмолошке дјелатности; истраживање атмосфере, водних ресурса, квалитета ваздуха и вода и сеизмолошких процеса; прикупљање, обрађивање и објављивање хидрометеоролошких и сеизмолошких података од интереса за Републику и вршење и других послова у области хидрологије, метеорологије и сеизмологије. Као самостална републичка управна организација Завод је обављао своју дјелатност све до 2002. године од када је у саставу Министарства пољопривреде, шумарства и водопривреде. Садашњи статус Завода је регулисан Законом о републичкој управи („Службени гласник Републике Српске“, број: 115/18).</p>', '2022-09-20 16:41:06', '2022-09-20 17:02:38', null);
INSERT INTO rhmzrs.pages (id, title, slug, html_content, created_at, updated_at, deleted_at) VALUES (3, 'Организациона шема', 'organizaciona-sema', '<p>Правилник о унутрашњој организацији и систематизацији радних мјеста у Републичком хидрометеоролошком заводу („Службени гласник Републике Српске“, бр. 76/17 и 06/18)</p><figure class="image"><img src="https://rhmzrs.com/wp-content/uploads/2018/09/organizaciona-sema.png" alt=""></figure>', '2022-09-20 17:10:27', '2022-09-20 17:10:27', null);
INSERT INTO rhmzrs.pages (id, title, slug, html_content, created_at, updated_at, deleted_at) VALUES (4, 'Извјештавање', 'izvjestavanje', '<p>У складу са прописима Свјетске метеоролошке организације (WMO) свакодневно се, за потребе многобројних корисника (министарства, привредни субјекти, медији, шира друштвена заједница и други),&nbsp;раде:</p><ul><li>Три метеоролошка билтена која садрже преглед и прогнозу метеоролошких услова за Републику Српску за текући дан и за наредна три дана.</li><li>Хидролошки билтен који садржи податке о водостању на ријекама у Републици Српској измјерених у 08:00 часова текућег дана</li></ul><p>Поред редовних дневних извјештаја у случајевима ванредних дешавања, земљотреса, поплава и&nbsp; екстремних климатских појава раде се ванредни билтени, а њихов број зависи од интензитета појаве, односно степена опасности по људе и материјална добра.</p><p>Континуирано се израђују седмодневни, декадни и мјесечни климатолошки и агрометеоролошки извјештаји те дневни и мјесечни извјештаји о квалитету ваздуха из области заштите животне средине. У области заштите животне средине се такође израђују Инвентари гасова са ефетком стаклене баште за Републику Српску и Годишњи извјештаји о регистру постројења и загађивача Републике Српске. Сви извјештаји из дјелокруга рада Завода се штампају у завршном годишњем извјештају.</p>', '2022-09-20 17:12:00', '2022-09-20 17:12:00', null);
INSERT INTO rhmzrs.pages (id, title, slug, html_content, created_at, updated_at, deleted_at) VALUES (5, 'Линкови ка другим институцијама', 'linkovi-ka-drugim-institucijama', '<h3>Институције Републике Српске</h3><ul><li>Предсједник Републике Српске:&nbsp;<a href="http://www.predsjednikrs.net/">http://www.predsjednikrs.net</a></li><li>Скупштина Републике Српске:&nbsp;<a href="http://www.narodnaskupstinars.net/">http://www.narodnaskupstinars.net/</a></li><li>Влада Републике Српске:&nbsp;<a href="http://www.vladars.net/">http://www.vladars.net</a></li><li>Агенција за воде обласног ријечног слива Саве:&nbsp;<a href="http://www.voders.org/">http://www.voders.org/</a></li><li>Агенција за воде обласног ријечног слива Требишњице:&nbsp;<a href="http://vodeherc.org/">http://vodeherc.org/</a></li></ul><h3>Институције Босне и Херцеговине</h3><ul><li>Федерални хидрометеоролошки завод:&nbsp;<a href="http://www.fhmzbih.gov.ba/">http://www.fhmzbih.gov.ba/</a></li><li>Агенција за водно подручје ријеке Саве: <a href="http://www.voda.ba/">http://www.voda.ba/</a></li><li>Агенција за водно подручје Јадранског мора:&nbsp;<a href="http://www.jadran.ba/">http://www.jadran.ba/</a></li></ul><h3>Институције сусједних држава</h3><ul><li>Републички Хидрометеоролошки завод Србије:&nbsp;<a href="http://www.hidmet.gov.rs/">http://www.hidmet.gov.rs/</a></li><li>Републички сеизмолошки завод Србије:&nbsp;<a href="http://www.seismo.gov.rs/">http://www.seismo.gov.rs/</a></li><li>Завод за хидрометеорологију и сеизмологију Црне Горе:&nbsp;<a href="http://www.meteo.co.me/">http://www.meteo.co.me/</a><ul><li>Сектор за сеизмологију:&nbsp;<a href="http://www.seismo.co.me/">http://www.seismo.co.me/</a></li></ul></li><li>Државни хидрометеоролошки завод Хрватске:&nbsp;<a href="http://meteo.hr/">http://meteo.hr/</a></li></ul>', '2022-09-20 17:20:57', '2022-09-20 17:20:57', null);
