INSERT INTO rhmzrs.pages (id, title, slug, html_content, created_at, updated_at, deleted_at) VALUES (36, 'Mапа мјерних станица', 'kvalitet-vazduha-mapa-mjernih-stanica', null, '2022-10-13 17:04:15', '2022-10-13 17:04:15', null);
INSERT INTO rhmzrs.pages (id, title, slug, html_content, created_at, updated_at, deleted_at) VALUES (37, 'Мапа загађивача', 'registar-postrojenja-i-zagadivaca-mapa-zagadjivaca', '<p>Према&nbsp;Правилнику о методологији и начину вођења Регистра постројења и загађивача („Службени гласник Републике Српске“ број: 92/07), Републички хидрометеоролошки завод, као надлежна институција за вођење Регистра, има законску обавезу да омогући приступ јавности информацијама и учешће у питањима заштите животне средине.</p><p>Мапа загађивача омогућава лако и једноставно претраживање, приказ и преузимање информација из Регистра постројења и загађивача, по различитим критеријумима унапријед дефинисаним одговарајућом законском регулативом: називу постројења, координатама постројења, ријечном сливу, дјелатности постројења, загађујућој материји или врсти отпада, елементу животне средине (ваздух, вода, земљиште), преносу отпада или календарској години.</p>', '2022-10-13 17:11:19', '2022-10-13 17:11:19', null);
