INSERT INTO rhmzrs.posts (id, html_content, title, type, thumbnail_url, metadata, created_at, updated_at, deleted_at, page_id, id_old) VALUES (719, 'Дана 07.01.2021. године, мрежом сеизмолошких станица Републичког хидрометеоролошког завода РС лоциран је земљотрес у 03 часова и 50 минутa по локалном времену, са магнитудом МL=2.7 јединицe Рихтера и процијењеним интензитетом у епицентралној зони од 3-4 степена Меркалијеве скале, са епицентром у региону Љубиња.
Земљотрес овог интензитета не може изазвати штете на објектима, а становништво га у епицентралној зони може осјетити.', 'Земљотрес код Љубиња', 'post', '', null, '2021-01-07 10:26:32', null, null, 14, 19549);
